                            <div class="wpb_wrapper">
                                <div style="font-family: Open Sans, sans-serif;font-size: 14px;font-weight: 600;line-height: 1.6;text-align: center;background: linear-gradient(to right,#fe7023 0%, #ff4231 100%);padding: 20px;border-radius: 26px;color: #fff !important;box-shadow: 0 16px 40px 0 rgba(74, 149, 208, 0.25);">
                                    <h5 style="text-transform: uppercase; font-weight: 800; color: #fff !important;">Calculate how much you could be making a day with Survey Bounce</h5>
                                    <div style="margin: 20px 0; padding: 0 25px;">
                                        <div class="row">
                                            <div class="col-12" style="font-weight: bold;">

                                                # OF FOLLOWERS

                                                <center>
                                                    <input id="followers" style="display: block; line-height: 2rem;width: 100%;margin: 25px;padding: .5rem .8rem;transition: all .3s;border: 0;border-radius: 4px;background-color: #fff;color: #ff4430;font-size: 2rem;font-weight: 400;resize: none;max-width: 200px;text-align: center;" type="tel"></center></div>
                                        </div>
                                        <center><input onclick="calculateB();" style="margin-top: 20px;background: #fff;padding: 10px;border-radius: 25px;border: 0;color: #fe592b;font-weight: 600;" type="button" value="Show Me"></center>

                                    </div>
                                    <p style="margin-bottom: 0; font-weight: 800; color: #fff">YOU CAN MAKE</p>

                                    <div>
                                        <p id="result" style="font-size: 35px; color: #fff">$<span id="money" style="font-weight: bold;">0</span><span style="font-size: 10px; vertical-align: baseline;">/day</span></p>

                                    </div>
                                </div>
                                <script>

                                    function animateValue(id, start, end, duration) {
                                        // assumes integer values for start and end

                                        var obj = document.getElementById(id);
                                        var range = end - start;
                                        // no timer shorter than 50ms (not really visible any way)
                                        var minTimer = 50;
                                        // calc step time to show all interediate values
                                        var stepTime = Math.abs(Math.floor(duration / range));

                                        // never go below minTimer
                                        stepTime = Math.max(stepTime, minTimer);

                                        // get current time and calculate desired end time
                                        var startTime = new Date().getTime();
                                        var endTime = startTime + duration;
                                        var timer;

                                        function run() {
                                            var now = new Date().getTime();
                                            var remaining = Math.max((endTime - now) / duration, 0);
                                            var value = Math.round(end - (remaining * range));
                                            obj.innerHTML = value;
                                            if (value == end) {
                                                clearInterval(timer);
                                            }
                                        }

                                        timer = setInterval(run, stepTime);
                                        run();
                                    }

                                    function calculateB() {

                                        followers = document.getElementById("followers").value;
                                        if(followers > 0) {
                                            followers = document.getElementById("followers").value;
                                        } else {
                                            followers = 0;
                                        }
                                        followersclean = parseFloat(followers.replace(/,/g, ''));
                                        //followers = document.getElementById("followers").value;
                                        cons = 0 + followersclean / 10;
                                        calc = 5 + cons + followersclean * 0.125;
                                        animateValue("money", 0, Math.round(calc), 1500);
                                        //document.getElementById("result").innerHTML = "$" + lol + '<span style=" font-size: 10px; vertical-align: baseline;">/day</span>';
                                    }

                                    $('#calculate').click(function(){
                                        calculateB();
                                    });


                                </script>
